SET mv_pos=f_lft-t_rgt+count_pos;
UPDATE `menu`
SET
m_rk = m_rk+count_pos,
m_lk = IF(m_lk > t_lft, m_lk+count_pos, m_lk)
WHERE m_rk >= t_rgt;
UPDATE `menu`
SET m_rk = m_rk - mv_pos
WHERE m_lk >= (f_lft + count_pos) AND m_rk <= (f_rgt + count_pos);
UPDATE `menu`
SET
m_lk = m_lk - mv_pos,
m_level = m_level - mv_level
