CREATE DEFINER=`mc`@`%` PROCEDURE `blog_image_reorder`(IN inId INT)
BEGIN
	DECLARE done, res, pos, biId INT DEFAULT 0;
    
    /*Объявление курсора*/
	DECLARE getImageIds CURSOR FOR 
    SELECT bi_id
    FROM `blog_image` 
    WHERE b_id = inId
    ORDER BY bi_pos, bi_update_ts DESC, bi_id DESC;
    
	DECLARE EXIT HANDLER FOR SQLWARNING ROLLBACK;
    DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done=1;
    
    SET done=0;
    SET pos=0;
    
    START TRANSACTION;
		/* открытие курсора */
		OPEN getImageIds;
		/*извлекаем данные */
		REPEAT
        FETCH getImageIds INTO biId;
			#делаем нужные нам действия 
			IF NOT done THEN     
			  UPDATE `blog_image` SET 
			  bi_pos = pos
			  WHERE bi_id = biId;
			  
			  SET pos = pos + 1;
			END IF;
		UNTIL done END REPEAT;
		
		/*закрытие курсора */
		CLOSE getImageIds;
        
        SET done = 0;
        
        SELECT bi_pos INTO pos
        FROM `blog_image`
        WHERE b_id = inId
		ORDER BY bi_pos, bi_update_ts DESC, bi_id DESC
        LIMIT 1;
        
        IF NOT done AND pos = 1 THEN
			UPDATE `blog_image` SET 
			bi_pos = bi_pos-1
			WHERE b_id = inId;
        END IF;
        
    COMMIT;
END ;;
