CREATE DEFINER=`mc`@`%` PROCEDURE `blog_subject_after`(IN f_id INT, IN t_id INT, OUT res INT)
BEGIN
DECLARE f_lft,f_rgt,f_lev,t_lft,t_rgt,t_lev,done INT DEFAULT 0;
DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done=1;
DECLARE EXIT HANDLER FOR SQLWARNING ROLLBACK;
SET res=0;
START TRANSACTION;
SET done=0;
SELECT bs_lk,bs_rk,bs_level
INTO f_lft,f_rgt,f_lev
FROM `blog_subject`
WHERE bs_id=f_id;
IF t_id>0 THEN
SELECT bs_lk,bs_rk,bs_level
INTO t_lft,t_rgt,t_lev
FROM `blog_subject`
WHERE bs_level=f_lev AND bs_id=t_id;
IF NOT done THEN
SELECT bs_lk,bs_rk,bs_level
INTO t_lft,t_rgt,t_lev
FROM `blog_subject`
WHERE bs_level=f_lev AND bs_lk>t_rgt AND bs_rk>t_rgt
ORDER BY bs_lk ASC
LIMIT 1;
IF done THEN
SET t_lft = t_rgt+1;
SET t_rgt = t_lft+1;
SET done=0;
END IF;
END IF;
ELSE
SELECT bs_lk,bs_rk,bs_level
INTO t_lft,t_rgt,t_lev
FROM `blog_subject`
WHERE bs_lk<f_lft AND bs_rk>f_rgt AND bs_level<f_lev
ORDER BY bs_level DESC
LIMIT 1;
IF NOT done THEN
SELECT bs_lk,bs_rk,bs_level
INTO t_lft,t_rgt,t_lev
FROM `blog_subject`
WHERE bs_level=f_lev AND bs_lk>t_lft AND bs_rk<t_rgt
ORDER BY bs_lk ASC
LIMIT 1;
ELSE
SET done=0;
SELECT bs_lk,bs_rk,bs_level
INTO t_lft,t_rgt,t_lev
FROM `blog_subject`
WHERE bs_level=f_lev
ORDER BY bs_lk ASC
LIMIT 1;
END IF;
END IF;
IF NOT done THEN
SET t_rgt=t_lft;
SET t_lft=t_lft-1;
CALL `blog_subject_relocate`(f_lft,f_rgt,f_lev,t_lft,t_rgt,t_lev-1,res);
END IF;
IF res=0 THEN
ROLLBACK;
ELSE
COMMIT;
END IF;
END ;;
