CREATE DEFINER=`mc`@`%` PROCEDURE `blog_subject_relocate`(IN f_lft INT, IN f_rgt INT, IN f_lev INT, IN t_lft INT, IN t_rgt INT, IN t_lev INT, OUT res INT)
BEGIN
DECLARE count_pos,mv_level,mv_pos INT DEFAULT 0;
DECLARE EXIT HANDLER FOR SQLWARNING ROLLBACK;
SET res=0;
START TRANSACTION;
IF f_lft<f_rgt AND t_lft<t_rgt AND (f_lft>t_rgt OR f_rgt<t_rgt) THEN
SET count_pos=f_rgt-f_lft+1;
SET mv_level=f_lev-t_lev-1;

IF f_lft>t_rgt THEN
SET mv_pos=f_lft-t_rgt+count_pos;
UPDATE `blog_subject`
SET
bs_rk = bs_rk+count_pos,
bs_lk = IF(bs_lk > t_lft, bs_lk+count_pos, bs_lk)
WHERE bs_rk >= t_rgt;
UPDATE `blog_subject`
SET bs_rk = bs_rk - mv_pos
WHERE bs_lk >= (f_lft + count_pos) AND bs_rk <= (f_rgt + count_pos);
UPDATE `blog_subject`
SET
bs_lk = bs_lk - mv_pos,
bs_level = bs_level - mv_level
WHERE bs_lk >= (f_lft+count_pos) AND bs_rk <= (t_rgt+count_pos);
UPDATE `blog_subject`
SET
bs_rk = bs_rk - count_pos,
bs_lk = IF(bs_lk > (f_rgt+count_pos), bs_lk - count_pos, bs_lk)
WHERE bs_rk > (f_rgt+count_pos);
ELSE
SET mv_pos = f_lft - t_rgt;
UPDATE `blog_subject`
SET
bs_rk = bs_rk + count_pos,
bs_lk = IF(bs_lk > t_lft, bs_lk + count_pos, bs_lk)
WHERE bs_rk >= t_rgt;
UPDATE `blog_subject`
SET bs_lk = bs_lk - mv_pos
WHERE bs_lk >= f_lft AND bs_rk <= f_rgt;
UPDATE `blog_subject`
SET
bs_rk = bs_rk - mv_pos,
bs_level = bs_level - mv_level
WHERE bs_lk >= t_rgt AND bs_rk <= f_rgt;
UPDATE `blog_subject`
SET bs_rk = bs_rk - count_pos,
bs_lk = IF(bs_lk > f_rgt, bs_lk - count_pos, bs_lk)
WHERE bs_rk>f_rgt;
END IF;
END IF;
SET res=1;
COMMIT;
END ;;
