		WHERE inBsPid IN
		(
			SELECT bs_id 
			FROM `blog_subject`
			WHERE bs_lk >= rLk
			AND bs_rk <= rRk
		)
	) INTO bIntoSelf;
	
	START TRANSACTION;
	
	UPDATE `blog_subject`
	SET
	bs_alias	= inBsAlias,
	bs_name	= inBsName
	WHERE bs_id = inBsId;
	
	
	IF bIntoSelf=1 THEN
		SET res = 1;
		COMMIT;
	ELSE
		CALL blog_subject_move(inBsId, inBsPid, res);
		
		IF res=1 THEN
			CALL blog_subject_after(inBsId, inAfterId, res);
            
		END IF;	
		
		IF res=0 THEN
		
			ROLLBACK;
		ELSE
			COMMIT;
		END IF;
