CREATE DEFINER=`mc`@`%` PROCEDURE `events_image_reorder`(IN inEid INT)
BEGIN
	
	DECLARE done, res, pos, eiId INT DEFAULT 0;
    
    /*Объявление курсора*/
	DECLARE getImageIds CURSOR FOR 
    SELECT ei_id
    FROM `events_image` 
    WHERE e_id = inEid
    ORDER BY ei_pos, ei_update_ts DESC, ei_id DESC;
    
	DECLARE EXIT HANDLER FOR SQLWARNING ROLLBACK;
    DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done=1;
    
    SET done=0;
    SET pos=0;
    
    START TRANSACTION;
		/* открытие курсора */
		OPEN getImageIds;
		/*извлекаем данные */
		REPEAT
        FETCH getImageIds INTO eiId;
			#делаем нужные нам действия 
			IF NOT done THEN     
			  UPDATE `events_image` SET 
			  ei_pos = pos
			  WHERE ei_id = eiId;
			  
			  SET pos = pos + 1;
			END IF;
		UNTIL done END REPEAT;
		
		/*закрытие курсора */
		CLOSE getImageIds;
        
        SET done = 0;
        
        SELECT ei_pos INTO pos
        FROM `events_image`
        WHERE e_id = inEid
		ORDER BY ei_pos, ei_update_ts DESC, ei_id DESC
        LIMIT 1;
        
        IF NOT done AND pos = 1 THEN
			UPDATE `events_image` SET 
			ei_pos = ei_pos-1
			WHERE e_id = inEid;
        END IF;
        
    COMMIT;
