CREATE DEFINER=`mc`@`%` PROCEDURE `menu_update`(IN inMenuId INT, IN inMenuPid INT, IN inAfterId INT, IN inMenuPath VARCHAR(255), IN inMenuName VARCHAR(100), IN inMenuTitle TEXT, IN inMenuH1 TEXT, IN inMenuDesc TEXT, IN inCId INT, IN inType INT, IN inShow INT,OUT res INT)
BEGIN
	DECLARE rPid, rId, rLk, rRk, bIntoSelf INT DEFAULT 0;
    DECLARE isAdmin INT DEFAULT 1;
	
	DECLARE EXIT HANDLER FOR SQLWARNING ROLLBACK;
	SET res=0;
	
	
	SELECT 
	m_lk, m_rk INTO rLk, rRk
	FROM `menu`
	WHERE m_id = inMenuId;
	
	SELECT EXISTS (
		SELECT 1 
		FROM `menu`
		WHERE inMenuPid IN
		(
			SELECT m_id 
			FROM `menu`
			WHERE m_lk >= rLk
			AND m_rk <= rRk
		)
	) INTO bIntoSelf;
	
	START TRANSACTION;
	
	UPDATE `menu`
	SET
	m_path	= inMenuPath,
	m_name	= inMenuName,
	m_title	= inMenuTitle,
	m_h1	= inMenuH1,
	m_desc  = inMenuDesc,
	c_id	= inCId,
    m_type	= inType,
    m_show	= inShow
	WHERE m_id = inMenuId;
	
	
	IF bIntoSelf=1 THEN
		SET res = 1;
		COMMIT;
	ELSE
		CALL menu_move(inMenuId, inMenuPid, res);
		
		IF res=1 THEN
			CALL menu_after(inMenuId, inAfterId, res);
			
            SELECT 
			m_type INTO isAdmin
			FROM `menu`
			WHERE m_id = inMenuPid;
            
            UPDATE `menu`
			SET
			m_type	= isAdmin
			WHERE m_id = inMenuId;
            
		END IF;	
		
		IF res=0 THEN
		
			ROLLBACK;
		ELSE
			COMMIT;
		END IF;
	END IF;
