CREATE DEFINER=`mc`@`%` PROCEDURE `news_image_reorder`(IN inId INT)
BEGIN
	
	DECLARE done, res, pos, niId INT DEFAULT 0;
    
    /*Объявление курсора*/
	DECLARE getImageIds CURSOR FOR 
    SELECT ni_id
    FROM `news_image` 
    WHERE n_id = inId
    ORDER BY ni_pos, ni_update_ts DESC, ni_id DESC;
    
	DECLARE EXIT HANDLER FOR SQLWARNING ROLLBACK;
    DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done=1;
    
    SET done=0;
    SET pos=0;
    
    START TRANSACTION;
		/* открытие курсора */
		OPEN getImageIds;
		/*извлекаем данные */
		REPEAT
        FETCH getImageIds INTO niId;
			#делаем нужные нам действия 
			IF NOT done THEN     
			  UPDATE `news_image` SET 
			  ni_pos = pos
			  WHERE ni_id = niId;
			  
			  SET pos = pos + 1;
			END IF;
		UNTIL done END REPEAT;
		
		/*закрытие курсора */
		CLOSE getImageIds;
        
        SET done = 0;
        
        SELECT ni_pos INTO pos
        FROM `news_image`
        WHERE n_id = inId
		ORDER BY ni_pos, ni_update_ts DESC, ni_id DESC
        LIMIT 1;
        
        IF NOT done AND pos = 1 THEN
			UPDATE `news_image` SET 
			ni_pos = ni_pos-1
			WHERE n_id = inId;
        END IF;
        
    COMMIT;
END ;;
