DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done=1;
DECLARE EXIT HANDLER FOR SQLWARNING ROLLBACK;
SET res=0;
START TRANSACTION;
SET done=0;
SELECT ug_lk,ug_rk,ug_level
INTO f_lft,f_rgt,f_lev
FROM `users_groups`
WHERE ug_id=f_id;
IF t_id>0 THEN
SELECT ug_lk,ug_rk,ug_level
INTO t_lft,t_rgt,t_lev
FROM `users_groups`
WHERE ug_level=f_lev AND ug_id=t_id;
IF NOT done THEN
SELECT ug_lk,ug_rk,ug_level
INTO t_lft,t_rgt,t_lev
FROM `users_groups`
WHERE ug_level=f_lev AND ug_lk>t_rgt AND ug_rk>t_rgt
ORDER BY ug_lk ASC
LIMIT 1;
IF done THEN
SET t_lft = t_rgt+1;
SET t_rgt = t_lft+1;
SET done=0;
END IF;
END IF;
ELSE
SELECT ug_lk,ug_rk,ug_level
INTO t_lft,t_rgt,t_lev
FROM `users_groups`
WHERE ug_lk<f_lft AND ug_rk>f_rgt AND ug_level<f_lev
ORDER BY ug_level DESC
LIMIT 1;
IF NOT done THEN
SELECT ug_lk,ug_rk,ug_level
INTO t_lft,t_rgt,t_lev
FROM `users_groups`
WHERE ug_level=f_lev AND ug_lk>t_lft AND ug_rk<t_rgt
ORDER BY ug_lk ASC
LIMIT 1;
ELSE
SET done=0;
SELECT ug_lk,ug_rk,ug_level
INTO t_lft,t_rgt,t_lev
FROM `users_groups`
WHERE ug_level=f_lev
ORDER BY ug_lk ASC
LIMIT 1;
END IF;
END IF;
IF NOT done THEN
SET t_rgt=t_lft;
SET t_lft=t_lft-1;
CALL `users_groups_relocate`(f_lft,f_rgt,f_lev,t_lft,t_rgt,t_lev-1,res);
END IF;
