CREATE DEFINER=`mc`@`%` PROCEDURE `users_groups_update`(IN inId INT, IN inPid INT, IN inAftecId INT, IN inPath VARCHAR(255), IN inName VARCHAR(100), IN inDesc TEXT, IN inOnRegister INT, OUT res INT)
BEGIN
	DECLARE cPid, cId, cLk, cRk, bIntoSelf INT DEFAULT 0;
	
	DECLARE EXIT HANDLER FOR SQLWARNING ROLLBACK;
	SET res=0;
	
	SELECT 
	ug_lk, ug_rk INTO cLk, cRk
	FROM `users_groups`
	WHERE ug_id = inId;
	
	SELECT EXISTS (
		SELECT 1 
		FROM `users_groups`
		WHERE inPid IN
		(SELECT ug_id 
		FROM `users_groups`
		WHERE ug_lk >= cLk
		AND ug_rk <= cRk)
	) INTO bIntoSelf;
	
	START TRANSACTION;
	
	UPDATE `users_groups`
	SET
	ug_path	= inPath,
	ug_name	= inName,
	ug_desc	= inDesc,
    ug_on_register = inOnRegister
	WHERE ug_id = inId;
	
	
	if bIntoSelf=1 THEN
		SET res = 1;
		Commit;
	ELSE
		CALL users_groups_move(inId, inPid, res);
		
		IF res=1 THEN
			CALL users_groups_after(inId, inAftecId, res);
		END IF;	
		
		IF res=0 THEN
			ROLLBACK;
		ELSE
			COMMIT;
		END IF;
	END IF;
END ;;
