CREATE DEFINER=`mc`@`%` PROCEDURE `controller_after`(IN f_id INT, IN t_id INT, OUT res INT)
BEGIN
DECLARE f_lft,f_rgt,f_lev,t_lft,t_rgt,t_lev,done INT DEFAULT 0;
DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done=1;
DECLARE EXIT HANDLER FOR SQLWARNING ROLLBACK;
SET res=0;
START TRANSACTION;
SET done=0;
SELECT c_lk,c_rk,c_level
INTO f_lft,f_rgt,f_lev
FROM `controllers`
WHERE c_id=f_id;
IF t_id>0 THEN
SELECT c_lk,c_rk,c_level
INTO t_lft,t_rgt,t_lev
FROM `controllers`
WHERE c_level=f_lev AND c_id=t_id;
IF NOT done THEN
SELECT c_lk,c_rk,c_level
INTO t_lft,t_rgt,t_lev
FROM `controllers`
WHERE c_level=f_lev AND c_lk>t_rgt AND c_rk>t_rgt
ORDER BY c_lk ASC
LIMIT 1;
IF done THEN
SET t_lft = t_rgt+1;
SET t_rgt = t_lft+1;
SET done=0;
END IF;
END IF;
ELSE
SELECT c_lk,c_rk,c_level
INTO t_lft,t_rgt,t_lev
FROM `controllers`
WHERE c_lk<f_lft AND c_rk>f_rgt AND c_level<f_lev
ORDER BY c_level DESC
LIMIT 1;
IF NOT done THEN
SELECT c_lk,c_rk,c_level
INTO t_lft,t_rgt,t_lev
FROM `controllers`
WHERE c_level=f_lev AND c_lk>t_lft AND c_rk<t_rgt
ORDER BY c_lk ASC
LIMIT 1;
ELSE
SET done=0;
SELECT c_lk,c_rk,c_level
INTO t_lft,t_rgt,t_lev
FROM `controllers`
WHERE c_level=f_lev
ORDER BY c_lk ASC
LIMIT 1;
END IF;
END IF;
IF NOT done THEN
SET t_rgt=t_lft;
SET t_lft=t_lft-1;
CALL `controller_relocate`(f_lft,f_rgt,f_lev,t_lft,t_rgt,t_lev-1,res);
END IF;
IF res=0 THEN
ROLLBACK;
ELSE
COMMIT;
END IF;
END ;;
DELIMITER ;
