CREATE DEFINER=`mc`@`%` PROCEDURE `controller_down`(IN p_id INT, OUT res INT)
BEGIN
DECLARE f_lft,f_rgt,f_lev,t_lft,t_rgt,t_lev INT DEFAULT 0;
SET res=0;
SELECT c_lk,i_c_rk,c_level
INTO f_lft,f_rgt,f_lev
FROM `controllers`
