CREATE DEFINER=`mc`@`%` PROCEDURE `controller_update`(IN inId INT, IN inPid INT, IN inAfterId INT, IN inPath VARCHAR(255), IN inName VARCHAR(100), IN inDesc TEXT, OUT res INT)
BEGIN
	DECLARE rPid, rId, rLk, rRk, bIntoSelf INT DEFAULT 0;
	
	DECLARE EXIT HANDLER FOR SQLWARNING ROLLBACK;
	SET res=0;
	
	
	SELECT 
	c_lk, c_rk INTO rLk, rRk
	FROM `controllers`
	WHERE c_id = inId;
	
	
	SELECT EXISTS (
		SELECT 1 
		FROM `controllers`
		WHERE inPid IN
		(SELECT c_id 
		FROM `controllers`
		WHERE c_lk >= rLk
		AND c_rk <= rRk)
	) INTO bIntoSelf;
	
	START TRANSACTION;
	
	UPDATE `controllers`
	SET
	c_path	= inPath,
	c_name	= inName,
	c_desc	= inDesc
	WHERE c_id = inId;
	
	
	if bIntoSelf=1 THEN
		SET res = 1;
		Commit;
	ELSE
		CALL controller_move(inId, inPid, res);
		
		IF res=1 THEN
			CALL controller_after(inId, inAfterId, res);
		END IF;	
		
		IF res=0 THEN
			ROLLBACK;
		ELSE
			COMMIT;
		END IF;
	END IF;
END ;;
DELIMITER ;
