CREATE DEFINER=`mc`@`%` PROCEDURE `menu_create`(IN inMenuPid INT, IN inAfterId INT, IN inMenuPath VARCHAR(255), IN inMenuName VARCHAR(100), IN inMenuTitle TEXT, IN inMenuH1 TExt, IN inMenuDesc TEXT, IN inCId INT, OUT last_ins_id INT)
BEGIN
	DECLARE i_level INT DEFAULT 1;
	DECLARE i_right_key INT DEFAULT 1;
	DECLARE res INT DEFAULT 0;
	DECLARE EXIT HANDLER FOR SQLWARNING ROLLBACK;
	 
	SET last_ins_id=0;
	SET res=0;
	START TRANSACTION;
	
	SELECT IF(ISNULL(MAX(m_rk)+1), 1, MAX(m_rk)+1 ) INTO i_right_key
	FROM `menu`;
	
	INSERT INTO `menu`
	SET 
	m_level	= i_level, 
	m_lk	= i_right_key, 
	m_rk	= i_right_key + 1,
	m_path	= inMenuPath,
	m_name	= inMenuName,
