CREATE DEFINER=`mc`@`%` PROCEDURE `menu_move`(IN f_id INT, IN t_id INT, OUT res INT)
BEGIN
  DECLARE f_lft
  , f_rgt
  , f_lev
  , t_lft
  , t_rgt
  , t_lev
  , done INT DEFAULT 0 ;
  DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done = 1 ;
  DECLARE EXIT HANDLER FOR SQLWARNING ROLLBACK ;
  SET res = 0 ;
  SET max_sp_recursion_depth = 1 ;
  START TRANSACTION ;
  SET done = 0 ;
  SELECT 
    m_lk
    , m_rk
    , m_level INTO f_lft
    , f_rgt
    , f_lev 
  FROM
    `menu` 
  WHERE m_id = f_id ;
  IF t_id = 0 
  THEN 
  SELECT 
    IFNULL(MIN(m_lk), 1) - 1
    , IFNULL(MAX(m_rk), 2) + 1
    , 0 INTO t_lft
    , t_rgt
    , t_lev 
  FROM
    `menu` ;
  ELSE 
  SELECT 
    m_lk
    , m_rk
    , m_level INTO t_lft
    , t_rgt
    , t_lev 
  FROM
    `menu` 
  WHERE m_id = t_id ;
  END IF ;
  IF NOT done 
  THEN 
  UPDATE 
    `menu` 
  SET
    m_pid = t_id 
  WHERE m_id = f_id ;
  CALL `menu_relocate` (
    f_lft
    , f_rgt
    , f_lev
    , t_lft
    , t_rgt
    , t_lev
    , res
  ) ;
  END IF ;
  IF res = 0 
  THEN ROLLBACK ;
  ELSE COMMIT ;
  END IF ;
END ;;
DELIMITER ;
