CREATE DEFINER=`mc`@`%` PROCEDURE `menu_update`(IN inMenuId INT, IN inMenuPid INT, IN inAfterId INT, IN inMenuPath VARCHAR(255), IN inMenuName VARCHAR(100), IN inMenuTitle TEXT, IN inMenuH1 TEXT, IN inMenuDesc TEXT, IN inCId INT, OUT res INT)
BEGIN
	DECLARE rPid, rId, rLk, rRk, bIntoSelf INT DEFAULT 0;
	
	DECLARE EXIT HANDLER FOR SQLWARNING ROLLBACK;
	SET res=0;
	
	
	SELECT 
	m_lk, m_rk INTO rLk, rRk
	FROM `menu`
	WHERE m_id = inMenuId;
	
	
	SELECT EXISTS (
		SELECT 1 
		FROM `menu`
		WHERE inMenuPid IN
		(
			SELECT m_id 
			FROM `menu`
			WHERE m_lk >= rLk
			AND m_rk <= rRk
		)
	) INTO bIntoSelf;
	
	START TRANSACTION;
	
	UPDATE `menu`
	SET
	m_path	= inMenuPath,
	m_name	= inMenuName,
	m_title	= inMenuTitle,
	m_h1	= inMenuH1,
	m_desc  = inMenuDesc,
	c_id	= inCId
	WHERE m_id = inMenuId;
	
	
	IF bIntoSelf=1 THEN
		SET res = 1;
		COMMIT;
	ELSE
		CALL menu_move(inMenuId, inMenuPid, res);
		
		IF res=1 THEN
			CALL menu_after(inMenuId, inAfterId, res);
			
		END IF;	
		
		IF res=0 THEN
		
			ROLLBACK;
		ELSE
			COMMIT;
		END IF;
	END IF;
END ;;
DELIMITER ;
/*!50003 SET sql_mode              = @saved_sql_mode */ ;
/*!50003 SET character_set_client  = @saved_cs_client */ ;
/*!50003 SET character_set_results = @saved_cs_results */ ;
/*!50003 SET collation_connection  = @saved_col_connection */ ;
/*!50003 DROP PROCEDURE IF EXISTS `role_after` */;
/*!50003 SET @saved_cs_client      = @@character_set_client */ ;
/*!50003 SET @saved_cs_results     = @@character_set_results */ ;
/*!50003 SET @saved_col_connection = @@collation_connection */ ;
/*!50003 SET character_set_client  = utf8 */ ;
/*!50003 SET character_set_results = utf8 */ ;
/*!50003 SET collation_connection  = utf8_general_ci */ ;
/*!50003 SET @saved_sql_mode       = @@sql_mode */ ;
/*!50003 SET sql_mode              = 'NO_AUTO_VALUE_ON_ZERO' */ ;
